DROP TABLE IF EXISTS `bsi_bookings`;
CREATE TABLE `bsi_bookings` (
  `booking_id` int(11) NOT NULL auto_increment,
  `room_id` int(11) NOT NULL,
  `booking_time` datetime NOT NULL default '0000-00-00 00:00:00',
  `start_date` date NOT NULL default '0000-00-00',
  `end_date` date NOT NULL default '0000-00-00',
  `client_id` int(10) unsigned default NULL,
  `adult_count` int(2) NOT NULL default '0',
  `child_count` int(2) NOT NULL default '0',
  `extra_guest_count` int(2) NOT NULL default '0',
  `discount_coupon` varchar(50) default NULL,
  `total_cost` decimal(10,2) unsigned NOT NULL default '0.00',
  `payment_amount` decimal(10,2) NOT NULL default '0.00',
  `payment_gateway_id` int(10) NOT NULL,
  `payment_success` tinyint(1) NOT NULL default '0',
  `payment_txnid` varchar(100) default NULL,
  `paypal_email` varchar(500) default NULL,
  `language_code` varchar(4) NOT NULL,
  `special_id` int(10) unsigned NOT NULL default '0',
  `special_requests` text,
  `is_block` tinyint(1) NOT NULL default '0',
  `is_deleted` tinyint(1) NOT NULL default '0',
  `invoice` mediumtext,
  PRIMARY KEY  (`booking_id`),
  KEY `start_date` (`start_date`),
  KEY `end_date` (`end_date`),
  KEY `booking_time` (`discount_coupon`),
  KEY `FK_bkngs_room_id` (`room_id`),
  KEY `FK_client` (`client_id`),
  KEY `FK_payment_gateway` (`payment_gateway_id`),
  CONSTRAINT `FK_bkngs_room_id` FOREIGN KEY (`room_id`) REFERENCES `bsi_rooms` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `FK_client` FOREIGN KEY (`client_id`) REFERENCES `bsi_clients` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `FK_payment_gateway` FOREIGN KEY (`payment_gateway_id`) REFERENCES `bsi_payment_gateway` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=10053 DEFAULT CHARSET=utf8;

